  </div>
</template>
<script>
export default {
  data() {
    return {
      switchID: window.originalSwitchId || null
    };
  },

  methods: {
     /** Log-out Confirmation  */
    confirmLogOut() {
        this.$deleteAlertMessage.data.isLogout = true;
        this.$deleteAlertMessage.data.isDelete = false;
        this.$deleteAlertMessage.data.isChangeStatus = false;
        this.$deleteAlertMessage.data.itemId = null;
        this.$deleteAlertMessage.data.callback = this.userLogout;
    },
    async userLogout() {
      try {
        const response = await axios.post(
          `${this.$userAppUrl}sdcsmuser/logout`,
          {},
          { withCredentials: true }
        );
        if (response.status == 200 && response.data.redirect_at) {
          window.location.href = response.data.redirect_at;
        }

      } catch (error) {
        this.$alertMessage.success = false;
        this.$alertMessage.message = "Something went wrong, please try again.";
      }
    },

    isActive(url) {
      /* Get the current pathname */
      const currentPath = window.location.pathname;
      return currentPath.startsWith(url) && (currentPath[url.length] == '/' || currentPath.length == url.length);
    },

    /** To switch back to admin */
    async switchBackToAdmin() {
      try {
        show_ajax_loader();
        const response = await axios.post(`${this.$userAppUrl}sdcsmuser/switch-back-to-admin`);
        if (response.status == 200) {
          hide_ajax_loader();
          this.$alertMessage.success = true;
          this.$alertMessage.message = response.data.msg;
          setTimeout(() => {
              location.href = `${this.$userAppUrl}smarttiusadmin`;
          }, 1500);
        }
      } catch (error) {
        hide_ajax_loader();
        this.$alertMessage.success = false;
        this.$alertMessage.message = "Something went wrong, please try again.";
      }
    }
  }
};
</script>